import React, { Component } from 'react'
import * as ACTION_TYPES from '../store/actions/action_types'
import * as ACTIONS from '../store/actions/actions'

import { connect } from 'react-redux'

class Container_one extends Component {
    render() {
        return (
            <div>
                {console.log(this)}
                <button onClick={() => console.log(`stpropone = ${this.props.st_prop_one}`)}>Get State</button>
                <button onClick={() => this.props.actionToSucccess()}>Dispatch Action One</button>
                <button onClick={() => this.props.actionToSFailure()}>Dispatch Action Two</button>
            </div>
        )
    }
    actionToSucccess = () => dispatch(ACTIONS.SUCCESS);
    actionToSFailure =  () => dispatch(ACTIONS.FAILURE)

}

//get state from redux
function mapStateToProps(state) {
    return {
        st_prop_one: state.st_prop_one
    }
}

function mapDispatchToProps(dispatch) {
    return {
        actionToSucccess: () => dispatch(ACTIONS.SUCCESS),
        actionToSFailure: () => dispatch(ACTIONS.FAILURE),
    }

}

export default Container_one
